import React from 'react'
import { Link } from 'react-router-dom'
import LOGO from '../images/logo.png'
import { BsCheck } from "react-icons/bs"
import { BsX } from "react-icons/bs"
import { useState } from 'react'
import data from '../data.json'

export default function Quiz() {

  const results = data.results;
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);

	const btnOnclickAnswer = (correct_answer) => {
		if (correct_answer === results[currentQuestion.correct_answer]) {
			setScore(score + 1);
		}

		const nextQuestion = currentQuestion + 1;

		if (nextQuestion < results.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};

  const playAgain = () => {
    setScore(0);
    setCurrentQuestion(0);
    setShowScore(false);
  }
  
  return (
    <div className='app'>
			{showScore ? (
				<div className='score-section'>
					You scored {score} out of {results.length}
				</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{results.length}
						</div>
						<div className='question-text'>{results[currentQuestion].question}</div>
					</div>
					<div className="btn"   onClick={btnOnclickAnswer(results[currentQuestion].correct_answer)}>
            <button className='btn-true'>
              <BsCheck className='icons'></BsCheck>True
            </button>
            <button className='btn-false'>
              <BsX className='icons'></BsX>False
            </button>
          </div>
				</>
			)}
		</div>
  )
}
